mapper.select:
select article_no, user_id, subject, content, hit, date_format(register_time, '%Y-%m-%d %H:%i') register_time
from board
where article_no = #{article_No}

mapper.update:
update board
set hit = hit + 1
where article_no = #{articleNo}

update board
set subject = #{subject}, content = #{content}
where article_no = #{article_no}
