mapper.select:
select salt
from member
where user_id = #{userId}

select count(user_id)
from member
where user_id = #{userId}

select user_id, user_name, user_pass, email, birth_date, gender, address, join_date, salt
from member
where user_id = #{userId} and user_pass = #{userPass}

mapper.update:
update member
email = #{email}, user_name = #{userName},
where user_id = #{userId}

update member
set user_pass = #{userPass}
where user_id = #{userId}
