mapper.select:
select salt
from member
where user_id = #{user_id}

select user_name, user_id, email, join_date, gender, address, birth_date
from member
where user_id = #{user_id}

select count(user_id)
from member
where user_id = #{user_id}

select user_id, user_name, user_pass, email, birth_date, gender, address, join_date, salt
from member
where user_id = #{user_id} and user_pass = #{user_pass}

mapper.update:
update member
email = #{email}, user_name = #{user_name},
where user_id = #{user_id}

update member
set user_pass = #{user_pass}
where user_id = #{user_id}

update member
set token = #{token}
where user_id = #{user_id}

mapper.select:
select token
from member
where user_id = #{user_id}

mapper.update:
update member
set token = #{token, jdbcType=VARCHAR}
where user_id = #{user_id}
